import React, {useEffect, useState} from 'react'
import useForm from './useForm'
import './Registration.css'

const Registration  = ({submitForm}) =>{

    const {handleChange, handleFormSubmit, values, errors} = useForm(
        submitForm
    )

  return (
    //   <div>
    //       Hi this is signup page
    //   </div>

        
    <div className="container1">
        <div className="app-wrapper">
            <div>
                <h2 className="title">Create Account</h2>
            </div>
            <form className="form-wrapper">
                <div className="name">
                    <label className="label">Name</label>
                    <input className="input" 
                    type="text" 
                    name="fullname" 
                    placeholder="Enter name"
                    value={values.fullname} 
                    onChange={handleChange}/>
                    {errors.fullname && <p className="error">{errors.fullname}</p> }
                </div>
                <div className="email">
                    <label className="label">Email</label>
                    <input className="input" type="text"  name="email" placeholder="Enter Email" value={values.email} 
                    onChange={handleChange}/>
                    {errors.email && <p className="error">{errors.email}</p> }
                </div>
                <div className="password">
                    <label className="label">Password</label>
                    <input className="input" type="text" 
                    name="password" placeholder="Enter Password" value={values.password}
                    onChange={handleChange}/>
                {errors.password && <p className="error">{errors.password}</p>}
                </div>
                <div>
                    <button className="submit" onClick={handleFormSubmit}>Sign Up</button>
                </div>
            </form>
        </div>
    </div>
  )
}

export default Registration
