import React, {useState} from 'react'
import './QueryComponent.css'
function QueryComponent(props){
    const [click,setClick] = useState('');

    let classes = '';

    const clickHandler = (event) =>{
        if(event.target.click)
        {
            setClick(1);
            console.log("MAking1"+click);

            classes = "HelpPageAnswer";
        }
        else{
            setClick(0)
            console.log("MAking 0"+click);
        }
    }
    return(
        <div className="RighHelpPageFormMainContaner">                   
                  <p>{props.question}</p>  
                  <span className={classes} value={click} onClick={clickHandler}><i class="fa fa-chevron-circle-down" aria-hidden="true"></i></span>  
                  <p className="HelpPageAnswerSection">{props.answer}</p>             
        </div>
    );
}

export default QueryComponent;
