import React, {useState} from 'react'
import './QueryComponent.css'
function QueryComponent(props){
    const [click,setClick] = useState(0);

    const clickHandler = (event) =>{
        console.log(click);
        if(event.target.value == 0)
        {
            setClick(1);
            console.log(click);
        }
        else{
            setClick(0)
            console.log(click);
        }
    }
    return(
        <div className="RighHelpPageFormMainContaner">                   
                  <p>{props.question}</p>  
                  <span className="HelpPageAnswer" value={click} onClick={clickHandler}><i class="fa fa-chevron-circle-down" aria-hidden="true"></i></span>  
                  <p className="HelpPageAnswerSection">{props.answer}</p>             
        </div>
    );
}

export default QueryComponent;
